//   }
// ]

const App = () => {

  const [data, setData] = useState('');

  const onSaveData = (datas) => {
    setData(prevDatas => {
      // return [...prevDatas, { name: userName, age: userAge, id: Math.random().toString() }];
      return [...prevDatas, { ...datas, id: Math.random().toString() }];
    })
  }

  return (
    <div>
      <AddForm onAddDatas={onSaveData} />
      <ShowInfo items={data} />
    </div>
  );
}

export default App;
