import React from 'react';
import classes from './Input.module.css';

const Input = (props) => {
    <React.Fragment
        className={`${classes.control} ${props.isValid === false ? classes.invalid : ''
            }`}
    >
        <label htmlFor={props.id}>
            {props.label}
        </label>

        <input
            type={props.type}
            id={props.id}
            value={props.value}
            onChange={props.onChange}
            onBlur={props.onBlur}
        />
    </React.Fragment>
}

export default Input;
